(function(){
  function uniq(arr){ return Array.from(new Set(arr.filter(Boolean))); }
  function toLangs(countryCode){
    const cc = (countryCode||'').toLowerCase();
    return (window.EU_COUNTRY_LANGS && window.EU_COUNTRY_LANGS[cc]) ? window.EU_COUNTRY_LANGS[cc] : ['en'];
  }
  window.getQueries = function(categoryKey, countryCode){
    const cat = (window.categoryMap||{})[categoryKey];
    if(!cat){ return []; }
    const langs = toLangs(countryCode);
    let terms = [];
    // Push UI ru labels (useful for Cyrillic searches on some boards)
    if (cat['ru']) terms = terms.concat(cat['ru']);
    // Add preferred language(s)
    langs.forEach(l => { if (cat[l]) terms = terms.concat(cat[l]); });
    // Always include English as a broad fallback
    if (cat['en']) terms = terms.concat(cat['en']);
    return uniq(terms);
  };
})();


